-------------------------------------------------------
-- Table creation DDL
-------------------------------------------------------

CREATE TABLE "POST_COMMENT" (
    "ID" NUMBER(19, 0) CONSTRAINT "NN_#POST_COMM_$ID" NOT NULL ENABLE,
	"UUID" RAW(16) CONSTRAINT "NN_#POST_COMM_$UUID" NOT NULL ENABLE,
    "ENTITY_VERSION" NUMBER(19, 0) CONSTRAINT "NN_#POST_COMM_$ENTVER" NOT NULL ENABLE,
	"POST_ID" NUMBER(19, 0) CONSTRAINT "NN_#POST_COMM_$POSTID" NOT NULL ENABLE,
    "CONTENT" VARCHAR2(256 CHAR) CONSTRAINT "NN_#POST_COMM_$CONT" NOT NULL ENABLE,
    "UPVOTES" NUMBER(10, 0) CONSTRAINT "NN_#POST_COMM_$UPV" NOT NULL ENABLE,    
    CONSTRAINT "PK_#POST_COMM_$ID" PRIMARY KEY ("ID") ENABLE,
    CONSTRAINT "FK_#POST_COMM_#POST" FOREIGN KEY ("POST_ID") REFERENCES "POST" ("ID") ENABLE
);

-------------------------------------------------------
-- Table constraints
-------------------------------------------------------

-- Check constraint for ID. Ranges from 1 to JAVA Long.MAX_VALUE
ALTER TABLE "POST_COMMENT" ADD CONSTRAINT "CHK_#POST_COMM_$ID" CHECK ("ID" BETWEEN 1 AND 9223372036854775807) ENABLE;

-- Check constraint for UUID. The length must be exactly 16, but the "LENGTH" function
-- always returns the value doubled, so we're checking for length = 32
ALTER TABLE "POST_COMMENT" ADD CONSTRAINT "CHK_#POST_COMM_$UUID" CHECK (LENGTH("UUID") = 32) ENABLE;

-- Unique constraint for UUID
ALTER TABLE "POST_COMMENT" ADD CONSTRAINT "UK_#POST_COMM_$UUID" UNIQUE ("UUID") ENABLE;

-- Check constraint for ENTITY_VERSION. Ranges from 0 to JAVA Long.MAX_VALUE
ALTER TABLE "POST_COMMENT" ADD CONSTRAINT "CHK_#POST_COMM_$ENTVER" CHECK ("ENTITY_VERSION" BETWEEN 0 AND 9223372036854775807) ENABLE;

-- Check constraint for UPVOTES. Ranges from 0 to JAVA Integer.MAX_VALUE
ALTER TABLE "POST_COMMENT" ADD CONSTRAINT "CHK_#POST_COMM_$UPV" CHECK ("UPVOTES" BETWEEN 0 AND 2147483647) ENABLE;

